import React from 'react'
import "./style.css"
import { useSelector, useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { OnClickImage } from '../../../redux/todoSlice';
import { GetCompanyById } from '../../../api/companyDetail/api';
export default function Card(props) {
  const {companyName,companyId}=props
  const navigate = useNavigate();
  const count = useSelector((store) => store.counter);
  const dispatch=useDispatch();
  const [data, setdata] = useState([]);
    async function GetCompanyForDetail() {
        const res = await GetCompanyById({Id:count.Id});
        if (res.state) {
          console.log("success res");
          setdata(res.data[0])
        //   window.location.reload();
          console.log(res.data[0])
        } else {
          console.log("error");
        }
      }

      useEffect(() => {
        GetCompanyForDetail();

      }, []);
    return (
      <div className='Card-name' onClick={() => {
        GetCompanyForDetail();
        dispatch(OnClickImage({companyId}))
        navigate("/jobdetail")}}>
        
        <span className='Card-inside'>
          {companyName}
        </span>
        
      </div>  
    );
}
