import React, { useEffect, useState } from 'react'
import { GetCompanyById } from '../../api/companyDetail/api';
import "./style.css"

export default function Detail(props) {
    const [data, setdata] = useState([]);
    async function GetCompany() {
        const res = await GetCompanyById();
        if (res.state) {
          console.log("success res");
          setdata(res.data)
        //   window.location.reload();
          console.log(res.data)
        } else {
          console.log("error");
        }
      }

      useEffect(() => {
        GetCompany();

      }, []);
    const ls=useState(['Strong sense of ownership in whatever you do.',
                    'Strong problem-solving and analytical skills. Good hold on relevant data structures & algorithms along with space and time complexity concepts.',           
                    'Strong programming expertise in microservices framework such as SpringBoot or any other.',
                    'Strong in designing database concepts.',
                    'Strong technical aptitude in software design principles and good knowledge of CS fundamentals.',
                    'Demonstrated ability to convert technical requirements into code, test and deploy.',
                'Demonstrated ability to quickly learn and adapt new technologies.',
                'Demonstrated ability to mentor and train entry level SDEs.',
                'B Tech/M.Tech/MCA in Computer Science or equivalent from a reputed college.'])
    const listOfData=['companyId','companyName','website','location','jobType','experience','qualification','Batch','salary','point1','point2','point3','point4','point5']
    let i=0
    return (
        <div className='detail-cont'>
            <div className='company-name'>
                Amazone/software engineer
            </div>
            <div className='company-description'>
            Flipkart is an online retailer based in India. It was founded by Binny and Sachin Bansal in 2007, and the company is based in Bengaluru, Karnataka, India. The organizationâ€™s CEO since 2017 is Kalyan Krishnamurthy. Its parent company is Walmart. In accordance with the data produced in 2019, the corporation had a revenue of 43,615 crore and 30,000 employees overall.
            </div>
            <div className='requirment'>
                <span className='req-header'>Requirments</span>
                <span>
                <table>
                {
                        data?data.map((item)=>{
                            return (<tr>
                                
                                <td>{item.companyName}</td>
                            </tr>
                                );
                        }):<span></span>
                    }
                
                    
                    {/* <tr>
                        <td>Company name</td>
                        <td></td>
                    </tr>
                    <tr>
                        <td>Website</td>
                        <td>Flipkart.com</td>
                    </tr>
                    <tr>
                        <td>Location</td>
                        <td>Hyderabad</td>
                    </tr>
                    <tr>
                        <td>Job Type</td>
                        <td>Full time</td>
                    </tr>
                    <tr>
                        <td>Experience</td>
                        <td>0-2 Years (Ops Executive)</td>
                    </tr>
                    <tr>
                        <td>Qualification</td>
                        <td>Bachelors Degree/ BSC</td>
                    </tr>
                    <tr>
                        <td>Batch</td>
                        <td>2018/ 2019/ 2020/ 2021/ 2022</td>
                    </tr>
                    <tr>
                        <td>Salary</td>
                        <td>Up to 6 LPA (Expected)</td>
                    </tr> */}
                </table>
                </span>
            </div>
            <div className='required-skills'>
                <span className='req-header'>
                    required skills
                </span>
                <span className='list-of'>
                {ls.map((item)=>{
                    return (
                        <span className='element'>{item}</span>  
                    );
                })}
                </span>
            </div>
            <div>
                    click here to apply 
            </div>
        </div>

    );
}
